package jetbrains.mps.vcs;

/*Generated by MPS */

import jetbrains.mps.logging.Logger;
import jetbrains.mps.vfs.IFile;
import jetbrains.mps.smodel.SModel;
import jetbrains.mps.watching.ModelChangesWatcher;
import com.intellij.openapi.util.Computable;
import java.io.File;
import java.io.IOException;
import com.intellij.openapi.ui.Messages;
import jetbrains.mps.util.FileUtil;
import com.intellij.openapi.project.ProjectManager;
import com.intellij.openapi.project.Project;
import com.intellij.openapi.vfs.VirtualFile;
import jetbrains.mps.ide.vfs.VirtualFileUtils;
import jetbrains.mps.ide.dialogs.BaseDialog;
import jetbrains.mps.smodel.ModelAccess;
import jetbrains.mps.smodel.SModelDescriptor;
import jetbrains.mps.smodel.SModelRepository;
import jetbrains.mps.smodel.IOperationContext;
import jetbrains.mps.project.GlobalOperationContext;
import jetbrains.mps.project.ModuleContext;
import javax.swing.JFrame;
import com.intellij.openapi.wm.WindowManager;
import jetbrains.mps.vcs.integration.ModelDiffTool;
import jetbrains.mps.vcs.diff.ui.ModelDifferenceDialog;
import jetbrains.mps.vcs.diff.ui.SimpleDiffRequest;
import jetbrains.mps.vcs.diff.ui.OldModelDifferenceDialog;
import javax.swing.SwingUtilities;
import jetbrains.mps.vcs.diff.ui.OldMergeModelsDialog;

public class VcsHelper {
  private static final Logger LOG = Logger.getLogger(VcsHelper.class);

  public VcsHelper() {
  }

  public static boolean resolveDiskMemoryConflict(final IFile modelFile, final SModel inMemory) {
    return ModelChangesWatcher.instance().executeUnderBlockedReload(new Computable<Boolean>() {
      public Boolean compute() {
        try {
          File backupFile = VcsHelper.doBackup(modelFile, inMemory);
          if (modelFile.exists()) {
            return VcsHelper.showDiskMemoryQuestion(modelFile, inMemory, backupFile);
          } else {
            return VcsHelper.showDeletedFromDiskQuestion(inMemory, backupFile);
          }
        } catch (IOException e) {
          VcsHelper.LOG.error(e);
          throw new RuntimeException(e);
        }
      }
    });
  }

  private static boolean showDeletedFromDiskQuestion(SModel inMemory, File backupFile) {
    int result = Messages.showYesNoDialog("Model file for model " + inMemory + " was externally deleted from disk.\n" + "Backup of it was saved to \"" + backupFile.getAbsolutePath() + "\"\nDo you wish to restore it?", "Model Deleted Externally", Messages.getQuestionIcon());
    return result == 0;
  }

  private static boolean showDiskMemoryQuestion(IFile modelFile, SModel inMemory, File backupFile) {
    String message = "Changes have been made to " + inMemory + " model in memory and on disk.\n" + "Backup of both versions was saved to \"" + backupFile.getAbsolutePath() + "\"\n" + "Which version to use?";
    String title = "Model Versions Conflict";
    String diskVersion = "Load &File System Version";
    String memoryVersion = "Save &Memory Version";
    String showDiffDialog = "Show &Difference";
    String[] options = {diskVersion, memoryVersion, showDiffDialog};
    int result = Messages.showDialog(message, title, options, 0, Messages.getQuestionIcon());
    if (result == -1) {
      return VcsHelper.resolveDiskMemoryConflict(modelFile, inMemory);
    }
    if (options[result].equals(diskVersion)) {
      return false;
    } else
    if (options[result].equals(memoryVersion)) {
      return true;
    } else {
      return VcsHelper.openDiffDialog(modelFile, inMemory);
    }
  }

  private static File doBackup(IFile modelFile, SModel inMemory) throws IOException {
    File tmp = FileUtil.createTmpDir();
    VcsHelperUtil.writeContentsToFile(ModelUtils.modelToBytes(inMemory), modelFile.getName(), tmp, VcsHelper.FsMemoryMergeVersion.MEMORY.getSuffix());
    if (modelFile.exists()) {
      com.intellij.openapi.util.io.FileUtil.copy(new File(modelFile.getPath()), new File(tmp.getAbsolutePath(), modelFile.getName() + "." + VcsHelper.FsMemoryMergeVersion.FILE_SYSTEM.getSuffix()));
    }
    File zipfile = VcsHelperUtil.chooseZipFileForModelFile(modelFile.getName());
    zipfile.getParentFile().mkdirs();
    FileUtil.zip(tmp, zipfile);
    FileUtil.delete(tmp);
    return zipfile;
  }

  private static boolean openDiffDialog(IFile modelFile, final SModel inMemory) {
    try {
      final SModel onDisk = ModelUtils.readModel(com.intellij.openapi.util.io.FileUtil.loadFileBytes(new File(modelFile.getPath())), modelFile.getPath());
      return VcsHelper.showDiffDialog(onDisk, inMemory, modelFile, ProjectManager.getInstance().getOpenProjects()[0]);
    } catch (IOException e) {
      LOG.error(e);
    }
    return true;
  }

  private static boolean showDiffDialog(final SModel diskModel, final SModel memoryModel, IFile modelFile, final Project project) {
    final VirtualFile file = VirtualFileUtils.getVirtualFile(modelFile);
    LOG.assertLog(file != null);
    final BaseDialog dialog = ModelAccess.instance().runReadAction(new Computable<BaseDialog>() {
      public BaseDialog compute() {
        SModelDescriptor modelDescriptor = diskModel.getModelDescriptor();
        if (modelDescriptor == null) {
          modelDescriptor = memoryModel.getModelDescriptor();
          if (modelDescriptor == null) {
            modelDescriptor = SModelRepository.getInstance().getModelDescriptor(diskModel.getSModelFqName());
          }
        }
        IOperationContext context;
        if (modelDescriptor == null) {
          context = new GlobalOperationContext();
        } else {
          context = new ModuleContext(modelDescriptor.getModule(), project);
        }
        JFrame frame = WindowManager.getInstance().getFrame(project);
        if (ModelDiffTool.isNewDiffEnabled()) {
          return new ModelDifferenceDialog(diskModel, memoryModel, new SimpleDiffRequest(project, "Filesystem version (Read-Only)", "Memory Version"));
        } else {
          return new OldModelDifferenceDialog(context, frame, diskModel, memoryModel, "Disk Memory Diff", true, new String[]{"Filesystem version (Read-Only)", "Memory Version"});
        }
      }
    });
    dialog.showDialog();
    SwingUtilities.invokeLater(new Runnable() {
      public void run() {
        dialog.toFront();
      }
    });
    return true;
  }

  public static boolean showMergeDialog(final SModel base, final SModel mine, final SModel repo, IFile modelFile, final Project project) {
    final VirtualFile file = VirtualFileUtils.getVirtualFile(modelFile);
    LOG.assertLog(file != null);
    final OldMergeModelsDialog dialog = ModelAccess.instance().runReadAction(new Computable<OldMergeModelsDialog>() {
      public OldMergeModelsDialog compute() {
        IOperationContext context = new ModuleContext(base.getModelDescriptor().getModule(), project);
        return new OldMergeModelsDialog(context, base, mine, repo);
      }
    });
    dialog.showDialog();
    SwingUtilities.invokeLater(new Runnable() {
      public void run() {
        dialog.toFront();
      }
    });
    if (dialog.getResultModel() != null) {
      SModel result = dialog.getResultModel();
      byte[] bytes = ModelUtils.modelToBytes(result);
      ModelUtils.replaceWithNewModelFromBytes(file, bytes);
      return false;
    }
    return true;
  }

  public static   enum FsMemoryMergeVersion implements Version {
    FILE_SYSTEM("filesystem"),
    MEMORY("memory");

    private final String mySuffix;

    FsMemoryMergeVersion(String suffix) {
      mySuffix = suffix;
    }

    public String getSuffix() {
      return mySuffix;
    }
  }
}
