package jetbrains.mps.vcs.diff.ui;

/*Generated by MPS */

import jetbrains.mps.ide.dialogs.BaseDialog;
import jetbrains.mps.smodel.IOperationContext;
import jetbrains.mps.smodel.SModel;
import java.awt.HeadlessException;
import jetbrains.mps.ide.dialogs.DialogDimensionsSettings;
import javax.swing.JComponent;
import com.intellij.openapi.application.ApplicationManager;
import javax.swing.JOptionPane;
import com.intellij.openapi.ui.Messages;
import com.intellij.openapi.ui.DialogWrapper;

public class OldMergeModelsDialog extends BaseDialog {
  private OldMergeView myMergeView;
  private boolean myCanceled;

  public OldMergeModelsDialog(IOperationContext context, SModel base, SModel mineModel, SModel repoModel) throws HeadlessException {
    super(context.getMainFrame(), "Merge Model Changes : " + base.getSModelReference());
    myMergeView = new OldMergeView(context, base, mineModel, repoModel);
  }

  public DialogDimensionsSettings.DialogDimensions getDefaultDimensionSettings() {
    return new DialogDimensionsSettings.DialogDimensions(10, 10, 1000, 900);
  }

  protected JComponent getMainComponent() {
    return myMergeView;
  }

  @BaseDialog.Button(position = 0, name = "OK", mnemonic = 'O', defaultButton = true)
  public void ok() {
    if (!(myMergeView.isResolved())) {
      int result;
      String message = "You have unresolved conflicts.\nDo you want to exit and let MPS resolve them automatically\n(conflicted changes from server would be discarded)?";
      String title = "Exit And Resolve Conflicts Automatically?";
      if (ApplicationManager.getApplication().isUnitTestMode()) {
        result = JOptionPane.showConfirmDialog(myMergeView, message, title, JOptionPane.OK_CANCEL_OPTION | JOptionPane.WARNING_MESSAGE);
      } else {
        result = Messages.showOkCancelDialog(message, title, Messages.getWarningIcon());
      }
      if (result != DialogWrapper.OK_EXIT_CODE) {
        return;
      }
    }
    dispose();
  }

  @BaseDialog.Button(position = 1, name = "Cancel", mnemonic = 'C')
  public void cancel() {
    dispose();
    myCanceled = true;
  }

  public SModel getResultModel() {
    if (myCanceled) {
      return null;
    }
    return myMergeView.getResultModel();
  }
}
